let toggleFriendship=function(){$(".toggle-friendship").click((function(e){e.preventDefault(),$.ajax({type:"POST",url:$(".toggle-friendship").attr("href")}).done((function(e){1==e.data.removed?friendshipStatus="Remove Friend":friendshipStatus="Add Friend",$(".toggle-friendship").html(friendshipStatus)})).fail((function(e){console.log("error in completing the request",e)}))}))};toggleFriendship();